	public static int majorityElement(int[] num) {
        int N = num.length;
        Map<Integer,Integer> m = new HashMap<Integer,Integer>();
        for(int i = 0; i < N; i ++){
        	Integer freq  = m.get(num[i]);
        	m.put(num[i], freq == null ? 1:freq+1);
        }
        
        for(Integer i : m.keySet()){
        	if(m.get(i) >= N /2)
        		return i;
        }
        
        return - 1;
    }
	
	public static int solution2(int[] num){
		int count = 0;
		int N = num.length;
		int rand = 0;
		int c = 0;
		while(count <= N / 2){
			rand = (int) (Math.random() * (N-1));
			c++;
			for(int i = 0; i < N ; i ++){
				if(num[i] == num[rand])
					count ++;
			}
		}
		System.out.println(c);
		return num[rand];
	}
	
	public static int solution3(int[] num){
		int N = num.length;
		int[] bits = new int[32];
		
		for(int i = 0; i < 32; i ++){
			
			int c1 = 0;
			for(int j = 0; j < N; j ++){
				
				String binary = Integer.toBinaryString(num[j]);
				
				System.out.println(binary);
				if(binary.charAt(i) == '1')
					c1 ++;
			}
			
			if(c1 > N/2)
				bits[i] = 1;
			else
				bits[i] = 0;
		}
		
		int res = 0;
		for(int i = 31; i >= 0; i ++){
			res = 2 * res + bits[i];
		}
		return res;
	}
